import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { CheckCircle, Clock, TrendingUp, Users, Zap, ExternalLink, Copy, Check } from 'lucide-react';
import ReactMarkdown from 'react-markdown';

const ResultsDisplay = ({ results }) => {
  const [copiedIndex, setCopiedIndex] = useState(null);

  const copyToClipboard = async (text, index) => {
    try {
      await navigator.clipboard.writeText(text);
      setCopiedIndex(index);
      setTimeout(() => setCopiedIndex(null), 2000);
    } catch (err) {
      console.error('Failed to copy text: ', err);
    }
  };

  const parseSuggestions = (suggestions) => {
    // Split by numbered sections (1., 2., etc.)
    const projects = suggestions.split(/\n(?=\d+\.\s)/).filter(project => project.trim());
    return projects.map((project, index) => {
      const lines = project.trim().split('\n');
      const title = lines[0].replace(/^\d+\.\s*/, '').trim();
      const content = lines.slice(1).join('\n').trim();

      return {
        id: index,
        title,
        content,
        fullText: project.trim()
      };
    });
  };

  const projects = parseSuggestions(results.suggestions);

  return (
    <div className="space-y-6">
      {/* Header Stats */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="card"
      >
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <div className="text-center">
            <div className="flex items-center justify-center w-12 h-12 bg-blue-100 rounded-lg mx-auto mb-2">
              <TrendingUp className="w-6 h-6 text-blue-600" />
            </div>
            <div className="text-2xl font-bold text-gray-900">{results.jobs_analyzed}</div>
            <div className="text-sm text-gray-600">Jobs Analyzed</div>
          </div>

          <div className="text-center">
            <div className="flex items-center justify-center w-12 h-12 bg-green-100 rounded-lg mx-auto mb-2">
              <CheckCircle className="w-6 h-6 text-green-600" />
            </div>
            <div className="text-2xl font-bold text-gray-900">{results.quality_descriptions}</div>
            <div className="text-sm text-gray-600">Quality Descriptions</div>
          </div>

          <div className="text-center">
            <div className="flex items-center justify-center w-12 h-12 bg-purple-100 rounded-lg mx-auto mb-2">
              <Users className="w-6 h-6 text-purple-600" />
            </div>
            <div className="text-2xl font-bold text-gray-900">{results.historical_jobs_used}</div>
            <div className="text-sm text-gray-600">Historical Jobs</div>
          </div>

          <div className="text-center">
            <div className="flex items-center justify-center w-12 h-12 bg-orange-100 rounded-lg mx-auto mb-2">
              <Clock className="w-6 h-6 text-orange-600" />
            </div>
            <div className="text-2xl font-bold text-gray-900">{results.total_time}s</div>
            <div className="text-sm text-gray-600">Total Time</div>
          </div>
        </div>
      </motion.div>

      {/* AI Suggestions */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.1 }}
      >
        <div className="mb-6">
          <h2 className="text-2xl font-bold text-gray-900 mb-2">
            🧠 AI Portfolio Project Suggestions
          </h2>
          <p className="text-gray-600">
            Based on {results.jobs_analyzed} job listings, here are tailored project ideas to strengthen your application.
          </p>
        </div>

        <div className="space-y-6">
          {projects.map((project, index) => (
            <motion.div
              key={project.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
              className="card hover:shadow-md transition-shadow duration-200"
            >
              {/* Project Header */}
              <div className="flex items-start justify-between mb-4">
                <div className="flex-1">
                  <div className="flex items-center space-x-2 mb-2">
                    <div className="flex items-center justify-center w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-500 rounded-lg text-white text-sm font-bold">
                      {index + 1}
                    </div>
                    <h3 className="text-lg font-semibold text-gray-900">
                      {project.title}
                    </h3>
                  </div>
                </div>

                <button
                  onClick={() => copyToClipboard(project.fullText, index)}
                  className="flex items-center space-x-1 text-sm text-gray-500 hover:text-gray-700 transition-colors duration-200"
                >
                  {copiedIndex === index ? (
                    <>
                      <Check className="w-4 h-4" />
                      <span>Copied!</span>
                    </>
                  ) : (
                    <>
                      <Copy className="w-4 h-4" />
                      <span>Copy</span>
                    </>
                  )}
                </button>
              </div>

              {/* Project Content */}
              <div className="prose prose-sm max-w-none">
                <ReactMarkdown
                  components={{
                    h1: ({ children }) => <h1 className="text-lg font-semibold text-gray-900 mb-2">{children}</h1>,
                    h2: ({ children }) => <h2 className="text-base font-semibold text-gray-800 mb-2">{children}</h2>,
                    h3: ({ children }) => <h3 className="text-sm font-semibold text-gray-700 mb-1">{children}</h3>,
                    p: ({ children }) => <p className="text-gray-700 mb-3 leading-relaxed">{children}</p>,
                    ul: ({ children }) => <ul className="list-disc list-inside text-gray-700 mb-3 space-y-1">{children}</ul>,
                    ol: ({ children }) => <ol className="list-decimal list-inside text-gray-700 mb-3 space-y-1">{children}</ol>,
                    li: ({ children }) => <li className="text-gray-700">{children}</li>,
                    strong: ({ children }) => <strong className="font-semibold text-gray-900">{children}</strong>,
                    em: ({ children }) => <em className="italic text-gray-700">{children}</em>,
                    code: ({ children }) => <code className="bg-gray-100 px-1 py-0.5 rounded text-sm font-mono">{children}</code>,
                    pre: ({ children }) => <pre className="bg-gray-100 p-3 rounded-lg overflow-x-auto text-sm">{children}</pre>,
                    blockquote: ({ children }) => <blockquote className="border-l-4 border-blue-500 pl-4 italic text-gray-600">{children}</blockquote>,
                  }}
                >
                  {project.content}
                </ReactMarkdown>
              </div>

              {/* Project Tags */}
              <div className="mt-4 pt-4 border-t border-gray-200">
                <div className="flex items-center space-x-2 text-sm text-gray-500">
                  <Zap className="w-4 h-4" />
                  <span>AI-Generated Portfolio Project</span>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </motion.div>

      {/* Footer Info */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.3 }}
        className="card bg-gradient-to-r from-blue-50 to-purple-50"
      >
        <div className="text-center">
          <h3 className="text-lg font-semibold text-gray-900 mb-2">
            🎯 Next Steps
          </h3>
          <p className="text-gray-700 mb-4">
            Choose one or two projects that align with your skills and interests. Start with the one that excites you most!
          </p>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
            <div className="bg-white p-3 rounded-lg">
              <div className="font-semibold text-gray-900 mb-1">1. Research</div>
              <div className="text-gray-600">Study the project requirements and gather resources</div>
            </div>
            <div className="bg-white p-3 rounded-lg">
              <div className="font-semibold text-gray-900 mb-1">2. Build</div>
              <div className="text-gray-600">Implement the project step by step</div>
            </div>
            <div className="bg-white p-3 rounded-lg">
              <div className="font-semibold text-gray-900 mb-1">3. Showcase</div>
              <div className="text-gray-600">Document and present your work professionally</div>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default ResultsDisplay;
